import React, { useState } from "react";
import { Button, makeStyles, TextField} from "@material-ui/core";
import { TopHeader } from "./shared/TopHeader";
import chessBoard from "./hostGamePage/chessBoard.png";
//import { BackButton } from "./shared/BackButton";
//import welcomeImage.png from ./public

const styles = makeStyles({
  backButton: {
    marginTop: "10px",
  },
  body: {
    paddingTop: "50px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  PlayButton: {
    backgroundColor: "#C8BFE7",
    fontSize: "25px",
    marginTop: "30px",
    marginBottom: "30px",
    padding: "0 93px",
    "&:hover": {
      backgroundColor: "#8474BE",
    },
  },
  WelcomeBanner: {
    paddingTop: "10px",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
  TextArea: {
    paddingTop: "5px",
    paddingLeft: "20px",
    paddingRight: "20px",
    paragraph: true,
    display: "flex",
    alignItems: "left",
  }
  
});

export const HomePage = () => {
  const classes = styles({});
  return (
    <div>
      <TopHeader />
      <div className={classes.WelcomeBanner}>
        <h1>Welcome To Social Connect</h1>
        <img src={chessBoard} />
      </div>

      <div className={classes.body}>
        <Button className={classes.PlayButton}>
          Start Playing
        </Button>

        <h2>The best place to stay connected</h2>
      </div>

      <div class="flexbox-container">
        <div><h3>Connect with friends or strangers</h3></div>
        <div><h3>Communicate via text chat</h3></div>
        <div><h3>Play a Variety of Games</h3></div>
        <div><h3>More Features Coming Soon</h3></div>
      </div>

      
    </div>
  );
};
