import React, { useState } from "react";
import { Button, makeStyles, Text, Typography } from "@material-ui/core";
import { TopHeader } from "./shared/TopHeader";
import chessBoard from "./hostGamePage/chessBoard.png";
//import { BackButton } from "./shared/BackButton";
//import welcomeImage.png from ./public

const useStyles = makeStyles({
  backButton: {
    marginTop: "10px",
  },
  body: {
    paddingTop: "50px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  PlayButton: {
    backgroundColor: "#C8BFE7",
    fontSize: "25px",
    marginTop: "30px",
    marginBottom: "30px",
    padding: "0 93px",
    "&:hover": {
      backgroundColor: "#8474BE",
    },
  },
  WelcomeBanner: {
    paddingTop: "10px",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
  TextArea: {
    paddingTop: "10px",
    paragraph: true,
    display: "flex",
    alignItems: "left",
  }
  
});

export const HomePage = () => {
  const classes = useStyles({});
  return (
    <div>
      <TopHeader />
      <div className={classes.WelcomeBanner}>
        <h1>Welcome To Social Connect</h1>
        <img src={chessBoard} />
      </div>

      <div className={classes.body}>
        <Button className={classes.PlayButton}>
          Start Playing
        </Button>

        <h2>The best place to stay connected</h2>

        <TextArea>
        <h3> Connect with Friends or Strangers</h3>
        <body>
        Social Connect was designed to keep you connected with friends both new and old,
        and to help you make new connections through gameplay.
        Choose a game, find or make a new friend, and have fun!

        </body>
        </TextArea>


      </div>
    </div>
  );
};
