import React, { useState } from "react";
import { Button, makeStyles, TextField } from "@material-ui/core";
import { TopHeader } from "./shared/TopHeader";
import { BackButton } from "./shared/BackButton";
//import welcomeImage.png from ./public

const useStyles = makeStyles({
  backButton: {
    marginTop: "10px",
  },
  body: {
    paddingTop: "50px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  hostButton: {
    backgroundColor: "#C8BFE7",
    fontSize: "25px",
    marginTop: "75px",
    padding: "0 93px",
    "&:hover": {
      backgroundColor: "#8474BE",
    },
  },
  WelcomeBanner: {
    paddingTop: "10px",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
});

export const HomePage = () => {
  const classes = useStyles({});
  //const [username, setUsername] = useState("");
  //const handleChange = (event) => {
    //setUsername(event.target.value);
  //};
  //const validUsername = username ? false : true;
  return (
    <div>
      <TopHeader />
      <div className={classes.WelcomeBanner}>
        <h1>Welcome To Social Connect</h1>
      </div>

      <div className={classes.body}>
        <Button className={classes.hostButton}>
          Start Playing
        </Button>
        <h2>The best place to stay connected</h2>
        <TextField>
        label = "The best place to stay connected"
        </TextField>
      </div>
    </div>
  );
};
