import { Button, makeStyles } from "@material-ui/core";
import { TopHeader } from "./components/TopHeader";
import chessBoard from "./components/hostGamePage/chessBoard.png";
import Box from '@material-ui/core/Box';


const styles = makeStyles({
  body: {
    paddingTop: "50px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  playButton: {
    backgroundColor: "#C8BFE7",
    fontSize: "25px",
    marginTop: "10px",
    marginBottom: "30px",
    padding: "0 93px",
    "&:hover": {
      backgroundColor: "#8474BE",
    },
  },
  welcomeBanner: {
    fontSize: "40px",
    paddingTop: "5px",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
  box: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
  },
  miniBox: {
    paddingRight: "100px",
  }
  
})

export const HomePage = () => {
  const classes = styles({});
  return (
    <div>
      <TopHeader />
      <div className={classes.welcomeBanner}>
        <p>Welcome To Social Connect</p>
        <img src={chessBoard} />
      </div>
      
      <div className={classes.body}>
        <Button className={classes.playButton}>
          Start Playing
        </Button>
        
        <h2>The best place to stay connected</h2>
      </div>

      <div style={{ width: '100%' }}>
      <Box className={classes.box} p={1}> 
        <Box p={1} className={classes.miniBox}>
          <h2>Connect With Friends or Strangers</h2>
        </Box>
        <Box p={1} className={classes.miniBox}>
          <h2>Communicate via Chat</h2>
        </Box>
        <Box p={1} className={classes.miniBox}>
          <h2>Play a Variety of Games</h2>
        </Box>
        <Box p={1} className={classes.miniBox}>
          <h2>More Features Coming Soon</h2>
        </Box>
      </Box>

    </div>

    </div>
    
  );
};
