import React, { useState } from "react";
import { Button, makeStyles, TextField } from "@material-ui/core";
import { TopHeader } from "./shared/TopHeader";
import { Paragraph } from 'react-native-paper';
//import { BackButton } from "./shared/BackButton";
//import welcomeImage.png from ./public

const useStyles = makeStyles({
  backButton: {
    marginTop: "10px",
  },
  body: {
    paddingTop: "50px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  PlayButton: {
    backgroundColor: "#C8BFE7",
    fontSize: "25px",
    marginTop: "75px",
    marginBottom: "30px",
    padding: "0 93px",
    "&:hover": {
      backgroundColor: "#8474BE",
    },
  },
  WelcomeBanner: {
    paddingTop: "10px",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
  TextArea: {
    paddingTop: "10px",
    display: "flex",
    alignItems: "left",
    flexDirection: "column"

  }
  
});

export const HomePage = () => {
  const classes = useStyles({});
  return (
    <div>
      <TopHeader />
      <div className={classes.WelcomeBanner}>
        <h1>Welcome To Social Connect</h1>
      </div>

      <div className={classes.body}>
        <Button className={classes.PlayButton}>
          Start Playing
        </Button>

        <h2>The best place to stay connected</h2>
        
        <div className={classes.TextArea}>
          <Paragraph>Hey this is a text area!</Paragraph>
        </div>


        <div style={{ height: '10%' }}>
        Hello World! This is a test paragraph where we will put more words.
        </div>


      </div>
    </div>
  );
};
